mapper.select:
SELECT r.pk_role_id AS pkRoleId, r.role_name AS roleName, r.role_desc AS roleDesc, r.`status` AS status, r.create_time AS createTime, u.user_name AS createUserName, r.last_modify_time AS lastModifyTime, uu.user_name AS lastModifyUserName FROM rm_role r LEFT JOIN rm_user u ON u.user_id = r.create_user_id LEFT JOIN rm_user uu ON uu.user_id = r.last_modify_user_id
and r.pk_role_id=#{form.userId}
and r.role_name like '%${form.roleName}%'
and r.role_desc like '%${form.roleDesc}%'
and r.status=#{form.status}
and u.user_name=#{form.createUserName}
and uu.user_name=#{form.lastModifyUserName}
AND r.create_time >= #{form.startTime,jdbcType=TIMESTAMP}
AND r.create_time <= #{form.endTime,jdbcType=TIMESTAMP}
ORDER BY r.last_modify_time DESC

SELECT p.id AS pkUserRoleId, r.pk_role_id AS roleId, r.role_name AS roleName, r.role_desc AS roleDesc FROM rm_role r INNER JOIN rm_user_role_rel p ON p.role_id = r.pk_role_id WHERE r.`status` = 1 AND r.pk_role_id IN( SELECT up.role_id FROM rm_user_role_rel up WHERE up.user_id=#{curUserId} )
AND p.user_id=#{userId,jdbcType=VARCHAR}
UNION SELECT '' AS pkUserRoleId, r.pk_role_id AS roleId, r.role_name AS roleName, r.role_desc AS roleDesc FROM rm_role r WHERE r.`status` = 1 AND r.pk_role_id IN( SELECT up.role_id FROM rm_user_role_rel up WHERE up.user_id=#{curUserId} ) AND NOT EXISTS ( SELECT * FROM rm_user_role_rel p WHERE p.role_id = r.pk_role_id
AND p.user_id=#{userId,jdbcType=VARCHAR}
)

update.foreach:
update rm_role set
status = #{ur.status},
last_modify_time = #{ur.lastModifyTime},
last_modify_user_id = #{ur.lastModifyUserId}
where pk_role_id = #{ur.pkId}
