mapper.select:
SELECT r.pk_resource_id AS pkResourceId, r.parent_id AS parentId, r.resource_name AS resourceName, r.route_name AS routeName, r.component, r.resource_icon AS resourceIcon, r.resource_type AS resourceType, r.resource_url AS resourceUrl, r.sort, r.is_parent AS isParent FROM rm_resource r LEFT JOIN rm_role_resource_rel rr ON rr.resource_id = r.pk_resource_id LEFT JOIN rm_user_role_rel p ON p.role_id = rr.role_id
r.`status` = 1
AND p.user_id=#{userId,jdbcType=VARCHAR}
AND r.resource_type=#{resourceType}
ORDER BY r.sort ASC

