mapper.select:
SELECT
L.id,
u.user_name AS userName,
L.operation_content AS operationContent,
L.duration,
L.method,
L.params,
L.ip,
L.create_time AS createTime,
L.location
FROM rm_log L
LEFT JOIN rm_user u ON u.user_id = L.user_id


        AND L.pk_log_id=#{form.pkLogId,jdbcType=VARCHAR}



        AND (u.user_name LIKE #{form.keyWord,jdbcType=VARCHAR}
        OR L.operation_content LIKE #{form.keyWord,jdbcType=VARCHAR}
        OR L.method LIKE #{form.keyWord,jdbcType=VARCHAR}
        OR L.params LIKE #{form.keyWord,jdbcType=VARCHAR}
        OR L.ip LIKE #{form.keyWord,jdbcType=VARCHAR}
        OR L.location LIKE #{form.keyWord,jdbcType=VARCHAR}
        )


        AND L.create_time >= #{form.startTime,jdbcType=TIMESTAMP}


        AND L.create_time <= #{form.endTime,jdbcType=TIMESTAMP}


ORDER BY L.create_time DESC
