update.foreach:
update rm_dict set
status = #{ur.status},
last_modify_time = #{ur.lastModifyTime},
last_modify_user_id = #{ur.lastModifyUserId}
where pk_dict_id = #{ur.pkId}

mapper.select:
select
from rm_dict where pk_dict_id in
#{dramaId}

SELECT d.pk_dict_id AS pkDictId, d.dict_key AS dictKey, d.dict_value AS dictValue, d.column_name AS columnName, d.column_desc AS columnDesc, d.table_name AS tableName, d.table_desc AS tableDesc, d.`status`, d.create_time AS createTime, u.user_name AS createUserName, d.last_modify_time AS lastModifyTime, uu.user_name AS lastModifyUserName FROM rm_dict d LEFT JOIN rm_user u ON u.user_id = d.create_user_id LEFT JOIN rm_user uu ON uu.user_id = d.last_modify_user_id WHERE 1=1
and d.pk_dict_id=#{pkDictId}
and d.school_id=#{schoolId}
and d.dict_key like '%${dictKey}%'
and d.dict_value like '%${dictValue}%'
and d.column_name like '%${columnName}%'
and d.column_desc like '%${columnDesc}%'
and d.table_name like '%${tableName}%'
and d.table_desc like '%${tableDesc}%'
and d.status=#{status}
and u.user_name like '%${createUserName}%'
and uu.user_name like '%${lastModifyUserName}%'
AND d.create_time >= #{startTime,jdbcType=TIMESTAMP}
AND d.create_time <= #{endTime,jdbcType=TIMESTAMP}
ORDER BY d.last_modify_time DESC
