mapper.select:
SELECT db.pk_dorm_building_id AS pkDormBuildingId, db.dorm_building_type AS dormBuildingType, db.dorm_building_num AS dormBuildingNum, db.dorm_building_levels AS dormBuildingLevels, db.dorm_building_desc AS dormBuildingDesc, db.`status`, db.create_time AS createTime, u.user_name AS createUserName, db.last_modify_time AS lastModifyTime, uu.user_name AS lastModifyUserName, db.memo FROM rm_dorm_building db LEFT JOIN rm_user u ON u.user_id = db.create_user_id LEFT JOIN rm_user uu ON uu.user_id = db.last_modify_user_id WHERE 1=1
and db.pk_dorm_building_id=#{form.pkDormBuildingId}
and db.dorm_building_num like '%${form.dormBuildingNum}%'
and db.dorm_building_type=#{form.dormBuildingType}
and db.dorm_building_levels=#{form.dormBuildingLevels}
and db.status=#{status}
and u.user_name like '%${form.createUserName}%'
and uu.user_name like '%${form.lastModifyUserName}%'
AND db.create_time >= #{form.startTime,jdbcType=TIMESTAMP}
AND db.create_time <= #{form.endTime,jdbcType=TIMESTAMP}
ORDER BY db.last_modify_time DESC

update.foreach:
update rm_dorm_building set
status = #{ur.status},
last_modify_time = #{ur.lastModifyTime},
last_modify_user_id = #{ur.lastModifyUserId}
where pk_dorm_building_id = #{ur.pkId}
