mapper.select:
select
from rm_dorm where pk_dorm_id in
#{dramaId}

select
from rm_dorm where dorm_num in
#{dramaId}

SELECT d.pk_dorm_id AS pkDormId, db.dorm_building_num AS dormBuildingNum, d.building_levelth AS buildingLevelth, d.dorm_type AS dormType, d.dorm_num AS dormNum, d.dorm_desc AS dormDesc, d.`status`, d.create_time AS createTime, u.user_name AS createUserName, d.last_modify_time AS lastModifyTime, uu.user_name AS lastModifyUserName, d.memo FROM rm_dorm d LEFT JOIN rm_dorm_building db ON db.pk_dorm_building_id = d.dorm_building_id LEFT JOIN rm_user u ON u.user_id = d.create_user_id LEFT JOIN rm_user uu ON uu.user_id = d.last_modify_user_id WHERE 1=1
and dv.pk_dorm_id=#{pkDormId}
and db.dorm_building_num like '%${dormBuildingNum}%'
and d.dorm_num like '%${dormNum}%'
and d.building_levelth=#{buildingLevelth}
and d.dorm_type=#{dormType}
and d.status=#{status}
and u.user_name like '%${createUserName}%'
and uu.user_name like '%${lastModifyUserName}%'
AND d.create_time >= #{startTime,jdbcType=TIMESTAMP}
AND d.create_time <= #{endTime,jdbcType=TIMESTAMP}
ORDER BY d.last_modify_time DESC

update.foreach:
update rm_dorm set
status = #{ur.status},
last_modify_time = #{ur.lastModifyTime},
last_modify_user_id = #{ur.lastModifyUserId}
where pk_dorm_id = #{ur.pkId}
