mapper.select:
SELECT d.pk_dorm_id AS pkDormId, db.dorm_building_num AS dormBuildingNum, d.building_levelth AS buildingLevelth, d.dorm_type AS dormType, d.dorm_num AS dormNum, d.dorm_desc AS dormDesc, d.`status`, d.create_time AS createTime, u.user_name AS createUserName, d.last_modify_time AS lastModifyTime, uu.user_name AS lastModifyUserName, d.memo FROM rm_dorm d LEFT JOIN rm_dorm_building db ON db.pk_dorm_building_id = d.dorm_building_id LEFT JOIN rm_user u ON u.user_id = d.create_user_id LEFT JOIN rm_user uu ON uu.user_id = d.last_modify_user_id WHERE 1=1
and dv.pk_dorm_id=#{form.pkDormId}
and d.dorm_num like '%${form.dormNum}%'
and d.building_levelth=#{form.buildingLevelth}
and d.dorm_type=#{form.dormType}
and d.status=#{form.status}
and u.user_name like '%${form.createUserName}%'
and uu.user_name like '%${form.lastModifyUserName}%'
AND d.create_time >= #{form.startTime,jdbcType=TIMESTAMP}
AND d.create_time <= #{form.endTime,jdbcType=TIMESTAMP}
ORDER BY d.last_modify_time DESC
