mapper.select:
SELECT dr.id, dr.name, dr.rule_desc AS ruleDesc, dr.`status`, dr.create_time AS createTime, u.user_name AS createUserName, dr.last_modify_time AS lastModifyTime, uu.user_name AS lastModifyUserName, dr.memo FROM rm_dorm_rule dr LEFT JOIN rm_user u ON u.user_id = dr.create_user_id LEFT JOIN rm_user uu ON uu.user_id = dr.last_modify_user_id WHERE 1=1
and dr.id=#{form.id}
and dr.name like '%${form.ruleName}%'
and dr.status=#{form.status}
and u.user_name like '%${createUserName}%'
and uu.user_name like '%${form.lastModifyUserName}%'
AND dr.create_time >= #{form.startTime,jdbcType=TIMESTAMP}
AND dr.create_time <= #{form.endTime,jdbcType=TIMESTAMP}
ORDER BY dr.last_modify_time DESC

update.foreach:
update rm_dorm_rule set
status = #{ur.status},
last_modify_time = #{ur.lastModifyTime},
last_modify_user_id = #{ur.lastModifyUserId}
where id = #{ur.pkId}
