mapper.select:
SELECT ds.id, ds.dorm_num AS dormNum, ds.culture_score AS cultureScore, ds.discipline_score AS disciplineScore, ds.bed_score AS bedScore, ds.desk_score AS deskScore, ds.balcony_score AS balconyScore, ds.toilet_score AS toiletScore, ds.ground_score AS groundScore, ds.door_window_score AS doorWindowScore, ds.metope_score AS metopeScore, ds.total_score AS totalScore, ds.`status`, ds.create_time AS createTime, u.user_name AS createUserName, ds.last_modify_time AS lastModifyTime, uu.user_name AS lastModifyUserName, ds.memo FROM rm_dorm_score ds LEFT JOIN rm_dorm d ON d.dorm_num = ds.dorm_num LEFT JOIN rm_user u ON u.user_id = ds.create_user_id LEFT JOIN rm_user uu ON uu.user_id = ds.last_modify_user_id LEFT JOIN rm_dorm_building db ON db.id = d.dorm_building_id WHERE 1=1
and ds.id=#{form.id}
and d.dorm_num like '%${form.dormNum}%'
and ds.status=#{form.status}
and u.user_name like '%${form.createUserName}%'
and uu.user_name like '%${form.lastModifyUserName}%'
AND ds.create_time >= #{form.startTime,jdbcType=TIMESTAMP}
AND ds.create_time <= #{form.endTime,jdbcType=TIMESTAMP}
ORDER BY ds.last_modify_time DESC

update.foreach:
update rm_dorm_score set
status = #{ur.status},
last_modify_time = #{ur.lastModifyTime},
last_modify_user_id = #{ur.lastModifyUserId}
where id = #{ur.pkId}
