mapper.select:
SELECT dv.id, dr.name AS dormRuleName, d.dorm_num AS dormNum, s.student_name AS studentName, dv.violation_img_url AS violationImgUrl, dv.violation_reason AS violationReason, dv.solve_result AS solveResult, dv.create_time AS createTime, u.user_name AS createUserName, dv.last_modify_time AS lastModifyTime, uu.user_name AS lastModifyUserName, dv.memo FROM rm_dorm_violation dv LEFT JOIN rm_dorm_rule dr ON dr.id = dv.dorm_rule_id LEFT JOIN rm_dorm d ON d.id = dv.dorm_id LEFT JOIN rm_student s ON s.pk_student_id = dv.student_id LEFT JOIN rm_user u ON u.user_id = dv.create_user_id LEFT JOIN rm_user uu ON uu.user_id = dv.last_modify_user_id LEFT JOIN rm_dorm_building db ON db.id = d.dorm_building_id WHERE 1=1
and dv.id=#{form.id}
and dr.name like '%${form.dormRuleName}%'
and d.dorm_num like '%${form.dormNum}%'
and s.student_name like '%${form.studentName}%'
and dv.status=#{form.status}
and u.user_name=#{form.createUserName}
and uu.user_name=#{form.lastModifyUserName}
AND dv.create_time >= #{form.startTime,jdbcType=TIMESTAMP}
AND dv.create_time <= #{form.endTime,jdbcType=TIMESTAMP}
ORDER BY dv.last_modify_time DESC

SELECT dv.id, dr.name AS dormRuleName, d.dorm_num AS dormNum, s.student_name AS studentName, dv.violation_img_url AS violationImgUrl, dv.violation_reason AS violationReason, dv.solve_result AS solveResult, dv.create_time AS createTime, dv.memo FROM rm_dorm_violation dv LEFT JOIN rm_dorm_rule dr ON dr.id = dv.dorm_rule_id LEFT JOIN rm_dorm d ON d.id = dv.dorm_id LEFT JOIN rm_student s ON s.pk_student_id = dv.student_id LEFT JOIN rm_user u ON u.user_id = dv.create_user_id LEFT JOIN rm_user uu ON uu.user_id = dv.last_modify_user_id LEFT JOIN rm_dorm_student_rel ds ON ds.dorm_id = d.id WHERE 1=1
and ds.student_id=#{studentId}
and dv.id=#{form.id}
and dr.name like '%${form.dormRuleName}%'
and d.dorm_num like '%${form.dormNum}%'
and s.student_name like '%${form.studentName}%'
and dv.status=#{form.status}
ORDER BY dv.last_modify_time DESC

update.foreach:
update rm_dorm_violation set
status = #{ur.status},
last_modify_time = #{ur.lastModifyTime},
last_modify_user_id = #{ur.lastModifyUserId}
where id = #{ur.pkId}
