mapper.select:
SELECT r.pk_resource_id AS pkResourceId, r.parent_id AS parentId, rr.resource_name AS parentName, r.resource_name AS resourceName, r.resource_icon AS resourceIcon, r.resource_type AS resourceType, r.resource_url AS resourceUrl, r.perms, r.sort, r.`status`, r.is_parent AS isParent FROM rm_resource r LEFT JOIN rm_resource rr ON rr.pk_resource_id = r.parent_id WHERE 1=1
AND r.status=#{status,jdbcType=INTEGER}
ORDER BY r.sort ASC

update.foreach:
update rm_resource set
status = #{ur.status},
last_modify_time = #{ur.lastModifyTime},
last_modify_user_id = #{ur.lastModifyUserId}
where pk_resource_id = #{ur.pkId}
