mapper.select:
SELECT p.pk_privilege_id AS pkUserRoleId, r.pk_role_id AS roleId, r.role_name AS roleName, r.role_desc AS roleDesc FROM rm_role r INNER JOIN rm_user_privilege p ON p.subject_id = r.pk_role_id WHERE r.`status` = 1 AND r.pk_role_id IN( SELECT up.subject_id FROM rm_user_privilege up WHERE up.user_id=#{curUserId} AND up.subject_type = 3 )
AND p.user_id=#{userId,jdbcType=VARCHAR}
UNION SELECT '' AS pkUserRoleId, r.pk_role_id AS roleId, r.role_name AS roleName, r.role_desc AS roleDesc FROM rm_role r WHERE r.`status` = 1 AND r.pk_role_id IN( SELECT up.subject_id FROM rm_user_privilege up WHERE up.user_id=#{curUserId} AND up.subject_type = 3 ) AND NOT EXISTS ( SELECT * FROM rm_user_privilege p WHERE p.subject_id = r.pk_role_id
AND p.user_id=#{userId,jdbcType=VARCHAR}
)

update.foreach:
update rm_role set
status = #{ur.status},
last_modify_time = #{ur.lastModifyTime},
last_modify_user_id = #{ur.lastModifyUserId}
where pk_role_id = #{ur.pkId}
