mapper.select:
SELECT s.pk_student_id AS studentId, s.student_name AS studentName, s.student_num AS studentNum, s.mobile AS mobile, s.gender AS gender, s.email AS email, s.img_url AS imgUrl, s.create_time AS createTime FROM rm_student s WhERE s.`status` = 1
AND s.student_num=#{studentNum,jdbcType=INTEGER}
AND s.password=#{password,jdbcType=VARCHAR}

select
from rm_student where pk_student_id in
#{dramaId}

SELECT s.pk_student_id AS pkStudentId, s.student_name AS studentName, s.student_num AS studentNum, db.dorm_building_num AS dormBuildingNum, d.dorm_num AS dormNum, s.mobile, s.gender, s.email, s.`status`, s.create_time AS createTime, uu.user_name AS lastModifyUserName, s.last_modify_time AS lastModifyTime, u.user_name AS createUserName FROM rm_student s LEFT JOIN rm_dorm_student_rel drs ON drs.student_id = s.pk_student_id LEFT JOIN rm_dorm d ON d.pk_dorm_id = drs.dorm_id LEFT JOIN rm_dorm_building db ON db.pk_dorm_building_id = d.dorm_building_id LEFT JOIN rm_user u ON u.user_id = s.create_user_id LEFT JOIN rm_user uu ON uu.user_id = s.last_modify_user_id WHERE 1=1
and s.pk_student_id=#{pkStudentId}
and s.student_name like '%${form.studentName}%'
and s.student_num=#{form.studentNum}
and d.dorm_num like '%${form.dormNum}%'
and db.dorm_building_num like '%${form.dormBuildingNum}%'
and s.gender=#{form.gender}
and s.status=#{form.status}
and u.user_name like '%${form.createUserName}%'
and uu.user_name like '%${form.lastModifyUserName}%'
AND s.create_time >= #{form.startTime,jdbcType=TIMESTAMP}
AND s.create_time <= #{form.endTime,jdbcType=TIMESTAMP}
ORDER BY s.last_modify_time DESC

update.foreach:
update rm_student set
status = #{ur.status},
last_modify_time = #{ur.lastModifyTime},
last_modify_user_id = #{ur.lastModifyUserId}
where pk_student_id = #{ur.pkId}
